\r\n}\r\n\r\n.hentry ol ol ol,\r\n.comment-text ol ol ol { \r\n	list-style: 			lower-alpha; \r\n}\r\n\r\n\r\n/********************************************************************\r\n	<DL> Definition Lists in post and comment bodies\r\n********************************************************************/\r\n\r\n.hentry dl,\r\n.comment-text dl { \r\n	margin:					20px 0;\r\n}\r\n\r\n.hentry dt,\r\n.comment-text dt { \r\n	font-weight: 			bold; \r\n	margin:					10px 0;\r\n}\r\n\r\n.hentry dd,\r\n.comment-text dd { \r\n	margin-left:			0;\r\n	padding-left:			20px;\r\n}\r\n\r\n\r\n/********************************************************************\r\n	<TABLE> in post and comment bodies\r\n********************************************************************/\r\n\r\n.hentry table,\r\n.comment-text table {\r\n	border-collapse:		collapse;\r\n	border-bottom:			solid 1px #e7e7e7;\r\n	margin-bottom:			10px;\r\n}\r\n\r\n.hentry  table caption,\r\n.comment-text table caption {\r\n	color: 					#707070; \r\n	font-size: 				14px; \r\n	letter-spacing: 		1px;\r\n	text-transform: 		uppercase; \r\n	padding: 				4px;\r\n}\r\n\r\n.hentry  table th,\r\n.comment-text table th {\r\n	padding: 				6px 8px 6px 9px; \r\n	text-align: 			left;\r\n}\r\n\r\n.hentry  table td,\r\n.comment-text table td {\r\n	padding: 				5px 8px;\r\n	border-top:				solid 1px #e7e7e7;\r\n}\r\n\r\n/* zebra-stripe even table rows. Class \'alternate\' added with jQuery */\r\n.hentry  table tr.alternate td,\r\n.comment-text table tr.alternate td {\r\n	background: 			#f7f7f7;\r\n}\r\n\r\n\r\n/********************************************************************\r\n	<BLOCKQUOTE>\r\n********************************************************************/\r\n\r\nblockquote {\r\n	color: 					#777777;\r\n	border-left: 			5px solid #dddddd;\r\n	margin: 					15px 30px 0px 10px;\r\n	padding-left: 			20px;\r\n}\r\n\r\nblockquote p {\r\n\r\n}\r\n\r\n/********************************************************************\r\n	<PRE>\r\n******